mapper.select:
SELECT DISTINCT
	p.RESOURCE_ID,
	p.USER_ID,
	p.USER_NAME,
	p.PHONE
FROM DEMO_INDEX_TEST p
WHERE 1 = 1

AND p.USER_ID = #{USER_ID}


AND p.USER_NAME = #{USER_NAME}


AND p.PHONE = #{PHONE}

mapper.update:
UPDATE DEMO_INDEX_TEST SET 
	USER_ID = #{USER_ID},
	USER_NAME = #{USER_NAME},
	PHONE = #{PHONE}
	CREATE_USER = #{CREATE_USER}
	CREATE_TIME = #{CREATE_TIME}
	MODIFY_USER = #{MODIFY_USER}
	MODIFY_TIME = #{MODIFY_TIME}
WHERE RESOURCE_ID = #{RESOURCE_ID}
